class TestFastqParser(unittest.TestCase):

    def test_lines_plain(self):
        """Are the lines returned correctly from file?"""
        fqp = dinopy.FastqReader(testreads_path)
        lines = fqp.lines()

        self.assertNotEqual(list(lines), [])
        for classified_line, expected_line in zip(lines, expected_lines):
            line_type, line_value = classified_line
            expected_line_type, expected_line_value = expected_line
            self.assertEqual(line_type, expected_line_type)

    def test_lines_zipped(self):
        """Are the lines returned correctly from zipped file?"""
        fqp = dinopy.FastqReader(testreads_path_zipped)
        lines = fqp.lines()

        self.assertNotEqual(list(lines), [])
        for classified_line, expected_line in zip(lines, expected_lines):
            line_type, line_value = classified_line
            expected_line_type, expected_line_value = expected_line
            self.assertEqual(line_type, expected_line_type)

    def test_lines_stdin(self):
        """Are the lines returned correctly from stdin?"""
        fqp = dinopy.FastqReader(mock_stdin)
        lines = fqp.lines()

        self.assertNotEqual(list(lines), [])
        for classified_line, expected_line in zip(lines, expected_lines):
            line_type, line_value = classified_line
            expected_line_type, expected_line_value = expected_line
            self.assertEqual(line_type, expected_line_type)

    def test_reads(self):
        """Are reads correctly identified and returned?"""
        fqp = dinopy.FastqReader(testreads_path)
        reads = list(fqp.reads(quality_values=True, quality_tally=True, dtype=str))
        for (exp_name, exp_seq, exp_qvs), (seq, name, qvs) in zip(expected_reads_with_qvs_str, reads):
            self.assertEqual(exp_name, name)
            self.assertEqual(exp_seq, seq)
            self.assertEqual(list(exp_qvs), list(qvs))

        fqp = dinopy.FastqReader(testreads_path)
        reads = list(fqp.reads(quality_values=False, dtype=str))
        for (exp_name, exp_seq, exp_qvs), (seq, name) in zip(expected_reads_with_qvs_str, reads):
            self.assertEqual(exp_name, name)
            self.assertEqual(exp_seq, seq)

    def test_reads_without_names(self):
        """Are reads (w/o names) correctly identified and returned?"""
        fqp = dinopy.FastqReader(testreads_path)
        reads = list(fqp.reads(read_names=False, quality_values=True, quality_tally=True, dtype=str))
        for (_, exp_seq, exp_qvs), (seq, qvs) in zip(expected_reads_with_qvs_str, reads):
            self.assertEqual(exp_seq, seq)
            self.assertEqual(list(exp_qvs), list(qvs))  # Fails: list(exp_qvs) == []?!

        fqp = dinopy.FastqReader(testreads_path)
        reads = list(fqp.reads(read_names=False, quality_values=False, dtype=str))
        for (_, exp_seq, _), (seq) in zip(expected_reads_with_qvs_str, reads):
            self.assertEqual(exp_seq, seq)

