        for (exp_name, exp_seq, exp_qvs), (seq, name, qvs) in zip(expected_reads_with_qvs_str_long, reads):
            self.assertEqual(exp_name, name)
            self.assertEqual(exp_seq, seq)
            self.assertEqual(list(exp_qvs), list(qvs))

        fqp = dinopy.FastqReader(testreads_path_long)
